import React from 'react';
import ReactDOM from 'react-dom';

// Create a class and extend it with react.component
class App extends React.Component {
    // Get the constructor
    constructor(props) {
        super(props);

        // code state here
        this.state = { lat: null, errorMsg: ''};

        // Geolocation api
        window.navigator.geolocation.getCurrentPosition(
            position => {
                this.state = this.setState({ lat: position.coords.latitude })
            },
            err => {
                this.state = this.setState({ errorMsg: err.message })
            }
        )     
    };

    // A class is nothing without the render fuc 
    render () {
    // than it's time to render some jsx
        if (!this.state.errorMsg && this.state.lat) {
            return <div>Latitude: { this.state.lat }</div>
        }

        if (this.state.errorMsg && !this.state.lat) {
            return <div>Error: { this.state.errorMsg }</div> 
        }

        return <div>Loading...</div>
    };
};


ReactDOM.render(<App />, document.querySelector('#root'));
